import { WechatWorkOutlined ,SettingFilled} from '@ant-design/icons'
import { Avatar } from 'antd'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

export default function Tool() {
const [isHovered, setHovered] = useState(false);
const [isHovered1, setHovered1] = useState(false);
const [isHovered2, setHovered2] = useState(false);
const [isHovered3, setHovered3] = useState(false);
const [isHovered4, setHovered4] = useState(false);
  const handleMouseEnter = () => {
    setHovered(true);
  };
  const handleMouseLeave = () => {
    setHovered(false);
  };
  const handleMouseEnter1 = () => {
    setHovered1(true);
  };
  const handleMouseLeave1 = () => {
    setHovered1(false);
  };
  const handleMouseEnter2 = () => {
    setHovered2(true);
  };
  const handleMouseLeave2 = () => {
    setHovered2(false);
  };
  const handleMouseEnter3 = () => {
    setHovered3(true);
  };
  const handleMouseLeave3 = () => {
    setHovered3(false);
  };
  const handleMouseEnter4 = () => {
    setHovered4(true);
  };
  const handleMouseLeave4 = () => {
    setHovered4(false);
  };
  const navigate = useNavigate();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const showModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };
  return (
    <div style={{ background:'#24786D',height:'100vh',display:'flex',flexDirection:'column',alignItems:'center',}}>
       <div  onClick={()=>showModal}>
       <Avatar style={{width:48,height:48,border: '2px solid white'}} src={require('./image/avt.jpg')} />
       {isModalOpen && (
        <div className="modal" onClick={closeModal} style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: '300px', background: 'white', padding: '20px', borderRadius: '10px', zIndex: 1000 }}>
          <h2>Thinh</h2>
          <div style={{backgroundColor:'#CCCCCC',width:250,height:5}}></div>
          
        </div>
      )}
      {isModalOpen && (
        <div className="modal-backdrop" style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 999 }}></div>
      )}
       </div>
        
         <div  title='Tin nhắn' style={{width:64,height:64,display:'flex',alignItems:'center',justifyContent:'center',background: isHovered ? '#014452' : 'transparent'}}  
            onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
            <WechatWorkOutlined style={{fontSize:50}} onClick={()=> navigate('/home')}/>
        </div>
       <div title='Danh bạ' style={{width:64,height:64,display:'flex',alignItems:'center',justifyContent:'center',background: isHovered1 ? '#014452' : 'transparent'}}  
            onMouseEnter={handleMouseEnter1} onMouseLeave={handleMouseLeave1}>
            <WechatWorkOutlined style={{fontSize:50}} onClick={()=> navigate('/optionlist')} />
            
        </div>
       <div title='Cài đặt' style={{width:64,height:64,display:'flex',alignItems:'center',justifyContent:'center',background: isHovered2 ? '#014452' : 'transparent'}}  
            onMouseEnter={handleMouseEnter2} onMouseLeave={handleMouseLeave2}>
            <SettingFilled style={{fontSize:50}}/>
        </div>
      
    </div>
  )
}
